package com.syntax.class01;

public class LoopwhileTask {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=50;
		while(num>=20){
		System.out.print(num +" ");
		num--;
		}
		System.out.println("------");
		int c=2;
		while(c<=20) {
			System.out.print(c+" ");
			c+=2;
		}
		System.out.println("---------");
		int i=1;
		while(i<=20) {
			if(i % 2==0) {
				System.out.print(i + " ");
			}
			i++;
		}
				
		
		
     
	}
	

}
