package com.syntax.class01;

public class NonpermitiveDatatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String Fruit = "Apple";
		double price = 1.99;
		System.out.println("the price of the" + Fruit + "is" + price);
		Fruit = "mango";
		price = 2.99;
		System.out.println("the price of the" +Fruit + "is" + price);
		
        
	}

}
